mapper.update:
UPDATE product_customer_group_price SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.delete:
DELETE FROM product_customer_group_price


		AND id = #{id}


		AND product_id = #{productId}
