mapper.select:
SELECT 

FROM product_picture a



		AND a.id = #{id}


		AND a.product_id = #{productId}


		AND a.cover_url = #{coverUrl}


limit 1

SELECT 

FROM product_picture a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM product_picture a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE product_picture SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
