mapper.select:
SELECT 

FROM product_specification a

WHERE a.id = #{id}

SELECT 

FROM product_specification a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM product_specification a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE product_specification SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
