mapper.select:
SELECT 

FROM specification_group a

WHERE a.id = #{id}

SELECT 

FROM specification_group a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM specification_group a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE specification_group SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
